mapper.select:


SELECT A.* FROM (		
	SELECT * FROM table_board
	WHERE bdelete='N'
		and BLIST= #{blist}

	ORDER BY bidx desc) A
	LIMIT #{rowStart},#{rowEnd}

SELECT COUNT(bidx)
FROM table_board
WHERE bidx>0 and bdelete='N' and blist=#{blist}

mapper.update:
update table_board
set
btitle = #{btitle}
,bcontent = #{bcontent}
where bidx = #{bidx}

mapper.insert:

insert into table_board (			
	BTITLE
	,bcontent
	,bwdate
	,bhit
	,blist
	,bwriter			
	,MIDX
)values(			
	#{btitle}
	,#{bcontent}
	,now()			
	,0
	,#{blist}
	,#{mname}			
	,#{midx}
)



	select max(bidx)	
	from table_board

mapper.delete:
update table_board
set bdelete = 'Y'
where bidx = #{bidx}

mapper.update:
update table_board
set bhit = bhit + 1
where bidx = #{bidx}

mapper.insert:
