mapper.select:
SELECT mid, mpwd
  FROM table_member
 WHERE mid = #{mid}
   AND mpwd = #{mpwd}

SELECT COUNT(mid)
  FROM table_member
 WHERE mid = #{mid}

mapper.insert:
	 insert into table_member(  midx 
	 							,mid
   					       		, mpwd
   					       		, mname
   					       		, mnumber
   					       		, mphone
   					       		, maddrdetail
   					       		, mmaddzipnum
   					       		, memail
   					       		 	)
                  values(   midx_seq.nextval
                  			, #{mid} 
                 		   , #{mpwd}
                 		   , #{mname}
                 		   , #{mnumber}
                 		   , #{mphone}
                 		   , #{maddrdetail}
                 		   , #{mmaddzipnum}
                 		   , #{memail}
                 		   )
